INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'en', 'Filter unblocking search ads and self-promotion', 'Filter that unblocks searches (Duckduckgo, Google, Bing, Yahoo) and self-promo ads. Self-promotion definition: When an ad on a website is promoting this very website or other websites/social media/etc. closely related to it, such an ad is considered a self-promoting one.');
