INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'ar', 'فلتر إلغاء حظر الإعلانات المفيدة', 'يمكنك تصفية البحث عن عمليات حظر البحث (دك دك غو. و جوجل و .بنغ .و ياهو) وإعلانات الترويج الذاتي. تعريف الترويج الذاتي: عندما يروج إعلان على أحد مواقع الويب لهذا الموقع ذاته أو مواقع ويب أخرى / وسائل التواصل الاجتماعي / إلخ. ترتبط ارتباطًا وثيقًا به ، ويعتبر هذا الإعلان إعلانًا ذاتي الترويج');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'id', 'Penyaring iklan berguna', 'Penyaring yang membuka blokir pencarian (Duckduckgo, Google, Bing, Yahoo) dan iklan promosi diri. Definisi promosi diri: ketika iklan di situs web mempromosikan situs web ini atau media sosial lain yang terkait erat dengannya, iklan tersebut dianggap sebagai iklan yang mempromosikan diri.');
