INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'nl', 'Filter voor zinvolle advertenties', 'Filter dat advertenties deblokkeert die wellicht zinvol zijn voor gebruikers.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'pt', 'Filtro para desbloquear anúncios úteis', 'Filtro que desbloqueia as pesquisas (Duckduckgo, Google, Bing, Yahoo) e anúncios autopromocionais. Definição de autopromocional: quando um anúncio num site promove esse mesmo site ou outros websites / redes sociais / etc. intimamente relacionados a ele, esse anúncio é considerado autopromocional.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'no', 'Filter for ikke å blokkere nyttige annonser', 'Filtrer som fjerner blokkering av søk (Duckduckgo, Google, Bing, Yahoo) og selvfremmende annonser. Definisjon av selvfremmende annonser: når en annonse på en nettside fremmer denne nettsiden eller andre nettsider/sosiale medier og så videre, som er nært knyttet til den. Slike annonser betraktes som selvfremmende.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'tr', 'Yararlı reklamlar gösteren filtre', 'Arama sonucu (Duckduckgo, Google, Bing, Yahoo) ve sitelerin kendi tanıtım reklamlarının engelini kaldıran filtre. Sitelerin kendi tanıtım reklamının tanımı: Bir sitedeki reklam, bu sitenin veya onunla yakından alakalı diğer siteyi/sosyal medya bağlantısını vb. tanıtıyorsa, bu reklam kendi tanıtım reklamı olarak kabul edilir.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'es-es', 'Filtro para anuncios útiles', 'Filtro que desbloquea anuncios, que podría ser útil para los usuarios.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'pt-pt', 'Filtro para desbloquear anúncios úteis', 'Filtro que desbloqueia as pesquisas (Duckduckgo, Google, Bing, Yahoo) e anúncios autopromocionais. Definição de autopromocional: quando um anúncio num site promove esse mesmo site ou outros websites / redes sociais / etc. intimamente relacionados a ele, esse anúncio é considerado autopromocional.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'pl', 'Filtr użytecznych reklam', 'Filtr, który odblokowuje reklamy w wyszukiwarkach internetowych (Duckduckgo, Google, Bing, Yahoo) i autopromocję. Definicja autopromocji: gdy reklama na stronie promuje tę stronę lub inne strony internetowe/media społecznościowe/itp. ściśle z nią związane, reklama taka jest uważana za autopromocję.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'be', 'Фільтр карыснай рэкламы', 'Фільтр, які знімае блакаванне пошукавай рэкламы (Duckduckgo, Google, Bing, Yahoo) і ўласную рэкламу вэб-сайтаў (зніжкі, прома-акцыі і таму падобнае).');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'fr', 'Filtre pour les annonces utiles', 'Filtre qui débloque les annonces liées aux recherches (Duckduckgo, Google, Bing, Yahoo) et anonces autopublicitaires. Définition de autopublicité: quand l''annonce d''un site web fait la promotion dûdit site, soit des sites web, média sociaux et autres plateformes reliées.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'bg', 'Филтър за полезни реклами', 'Филтър който разблокира реклами, които могат да бъдат полезни за потребителите.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'pt-br', 'Filtro para desbloquear anúncios úteis e de autopromoção', 'Filtro que desbloqueia os anúncios de autopromoção dentro dos sites de buscas (Google, Duckduckgo, Bing, Yahoo) que podem ser útil para o usuário. Definição de autopromoção: É quando um anúncio em um site está promovendo o site dele mesmo ou outros sites relacionados a ele. Esse tipo de anúncio é considerado autopromocional.');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'hr', 'Filter za korisne oglase', 'Filter koji dozvoljava oglase koji bi mogli biti korisni');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'zh-tw', '解除封鎖搜尋和自我推銷廣告之過濾器', '解除封鎖搜尋（Duckduckgo、Google、Bing、Yahoo）和自我推銷廣告之過濾器。自我推銷的定義：當一個於網站上的廣告恰好正在推銷這個網站或與其密切相關之其它的網站 / 社群媒體 / 等等時，這樣的廣告被認為是一種自我推銷的廣告。');
INSERT INTO filters_localization (filter_list_id, language_code, filter_name, filter_description) VALUES (10, 'de', 'Filter zum Anzeigen von nützlicher Werbung', 'Filter, der Such- (DuckDuckGo, Google, Bing, Yahoo) und Eigenwerbung zulässt. Definition Eigenwerbung: Wenn eine Anzeige auf einer Website diese Website oder andere eng damit verbundene Websites / soziale Medien / etc. fördert, gilt diese Anzeige als selbstwerbend.');
